import React, {useState} from 'react'

import CancelIcon from '../.././svg/cancel2.svg';

const CoverPhotoModal = ({setModal, userInfo}) => {
    const [coverPhotoData, setcoverPhotoData] = useState(userInfo.user?.coverPhoto);

    const handleChange = (e) => {
        setcoverPhotoData(e.target.files[0]);
    }

    const handleSubmit = () => {

    }


    return (
        <div>
            <div className="fixed inset-0 z-40 w-full h-full flex justify-center items-center bg-black bg-opacity-25 overflow-auto" >
                <div className="bg-primary m-2 w-full md:w-2/4 h-56 shadow-md border border-borderColor rounded-md">
                    <div className="p-2 flex justify-between items-center border-b border-borderColor tracking-wide font-light">
                        <div className="p-1 text-lg">Change Cover Photo</div>
                    
                        <button className="hover:bg-white hover:bg-opacity-10 active:bg-white active:bg-opacity-25 rounded-full  p-2 transition ease-in duration-75" onClick={() => setModal(prev => !prev)}><img src={CancelIcon} alt="" className="h-4 w-4" /></button>
                    </div>

                    <div className="flex gap-2 items-center self-start m-3">
                        <p className="text-xs md:text-sm break-words ">Cover Photo:</p>
                        <form onSubmit={handleSubmit} className="text-xs md:text-sm relative overflow-hidden">
                            <input type="file" name="image" onChange={handleChange} />
                        </form>
                    </div>
                    {coverPhotoData && 
                        <div className="w-full h-40 overflow-auto relative">
                            <img src={coverPhotoData} alt="userProfileImg" className="rounded-md bg-purple-400 object-cover object-center" />
                            <button className="bg-gray-600 bg-opacity-40 shadow-md hover:bg-gray-500 hover:bg-opacity-30  active:bg-white active:bg-opacity-25 rounded-full p-2 transition ease-in duration-75 absolute -top-2 -right-2">  <img src={CancelIcon} alt="" className="w-4 h-4 rounded-md " onClick={() => setcoverPhotoData('')} /> </button>
                        </div>}
                </div>
            </div>
        </div>
    )
}

export default CoverPhotoModal
