import React, { useState } from 'react'

import { useDispatch } from 'react-redux';
import { updateUserProfile } from '../../redux/actions/users';

import CancelIcon from '../.././svg/cancel2.svg';
import loadingCat from '../.././svg/loadingCat.svg';
const EditUserProfile = ({setModal, userInfo}) => {

    const [updateData, setUpdateData] = useState({firstname: userInfo.user?.firstname, lastname: userInfo.user?.lastname,imageUrl: userInfo.user?.imageUrl, coverPhoto: userInfo.user?.coverPhoto});
    const dispatch = useDispatch();
  
    const handleUpdate = (e) => {
        let myForm = e.target;
        const formData = new FormData(myForm);
        dispatch(updateUserProfile(userInfo.user?._id, formData, setModal));
    }

    return (
        <div className="fixed inset-0 z-40 w-full h-full flex justify-center items-center bg-black bg-opacity-25 overflow-auto" >
            <div className="bg-primary m-2 w-full md:w-2/4 h-auto shadow-md border border-borderColor rounded-md">
                <div className="p-2 flex justify-between items-center border-b border-borderColor tracking-wide font-light">
                    <div className="p-1 text-lg">Edit name</div>
                   
                    <button className="hover:bg-white hover:bg-opacity-10 active:bg-white active:bg-opacity-25 rounded-full  p-2 transition ease-in duration-75" onClick={() => setModal(prev => !prev)}><img src={CancelIcon} alt="" className="h-4 w-4" /></button>
                </div>
                
                {/* form */}
                     {/* execute during update */}
                     {userInfo.userUpdateLoading ?
                        <div className="p-4 w-full h-full flex flex-col items-center justify-center space-y-2">
                            <img src={loadingCat} alt="" className="w-14 h-14" /> 
                            <p>Updating...Meow!!</p>
                            <p className="text-sm font-light text-gray-400">Full page reload after..</p>
                        </div> 
                        : 
                        <div className="p-2 space-y-2">
                            <form onSubmit={ handleUpdate}>
                            <div className="flex gap-2">
                                <div className="space-y-2 flex-1">
                                    <label>Firstname</label>
                                    <input type="text" placeholder="Firstname" value={updateData.firstname} className="w-full p-2 bg-secondary rounded-md outline-none" onChange={(e) => setUpdateData({...updateData, firstname: e.target.value})} name="firstname" />
                                </div>
                                <div className="space-y-2 flex-1">
                                    <label>Lastname</label>
                                    <input type="text" placeholder="Lastname" value={updateData.lastname} className="w-full p-2 bg-secondary rounded-md outline-none" onChange={(e) => setUpdateData({...updateData, lastname: e.target.value})} name="lastname" />
                                </div>
                            </div>
                            
                            <button className="bg-button2 mt-2 py-1 px-1 w-full text-center rounded-md text-md md:text-lg" >Save Changes</button>
                            </form>
                        </div>
                        }
                </div>
        </div>
    )

    const coverPhotoModal = () => (
    
        <div className="flex flex-col items-center gap-3">
            <div className="flex gap-2 items-center self-start">
                <p className="text-xs md:text-sm break-words ">Profile Picture:</p>
                <div className="text-xs md:text-sm relative overflow-hidden">
                    <input type="file" name="image" />
                </div>
            </div>
            {updateData.imageUrl && 
                <div className="relative">
                    <img src={updateData.imageUrl} alt="userProfileImg" className="h-24 w-24 rounded-full bg-purple-400 object-cover object-center" />
                    <button className="bg-gray-600 bg-opacity-40 shadow-md hover:bg-gray-500 hover:bg-opacity-30  active:bg-white active:bg-opacity-25 rounded-full p-2 transition ease-in duration-75 absolute -top-2 -right-2">  <img src={CancelIcon} alt="" className="w-4 h-4 rounded-md " onClick={() => setUpdateData({...updateData, imageUrl: ''})} /> </button>
                </div>}
        </div>
    )

    const profileModal = () => (
        <div className="flex flex-col justify-center gap-3">
            <div className="flex items-center gap-2">
                <p className="text-xs md:text-sm ">Cover Photo:</p>
                    <div className="text-xs md:text-sm relative overflow-hidden">
                        <input type="file" name="image" />
                    </div>
            </div>
            {updateData.coverPhoto &&   
                <div className="w-full h-40 overflow-auto relative">
                    <img src={updateData.coverPhoto} alt="" className="rounded-md bg-purple-400 object-cover object-center"/>
                    <button className="bg-gray-600 bg-opacity-30 shadow-md hover:bg-gray-500 hover:bg-opacity-30  active:bg-white active:bg-opacity-25 rounded-full p-2 transition ease-in duration-75 absolute top-0 right-0 m-2">  <img src={CancelIcon} alt="" className="w-4 h-4 rounded-md " onClick={() => setUpdateData({...updateData, coverPhoto: ''})} /> </button>
                </div>}
        </div>
    
    )
     
    
}
//Components for modals cover-photo profile









export default EditUserProfile
